import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;


public class Main {

    public static void main(String[] args) {

        String DNA="";
        String startCodon="ATG";
        String[] stopCodons={"TAA","TGA","TAG"};

        List<Integer> indexesOfATG  =new ArrayList<Integer>();
        List<Integer> indexesOfTAA  =new ArrayList<Integer>();
        List<Integer> indexesOfTGA  =new ArrayList<Integer>();
        List<Integer> indexesOfTAG  =new ArrayList<Integer>();



        int indexOfStartCodon= DNA.indexOf(startCodon); //finds ATG index in DNA String

        int indexOfStopCodon1 = DNA.indexOf(stopCodons[0]);
        int indexOfStopCodon2  = DNA.indexOf(stopCodons[1]);
        int indexOfStopCodon3 = DNA.indexOf(stopCodons[2]);


        System.out.println("INDEXES OF START CODONS:");

        while(indexOfStartCodon != -1){

            indexesOfATG.add(indexOfStartCodon);
            System.out.println("index of start codon ATG: "+indexOfStartCodon);
            indexOfStartCodon= DNA.indexOf(startCodon, indexOfStartCodon+3);

        }
        System.out.println("-----------------------");

        System.out.println("INDEXES OF STOP CODONS:");

        while(indexOfStopCodon1 != -1){

            indexesOfTAA.add(indexOfStopCodon1);
            System.out.println("index of stop codon TAA: "+indexOfStopCodon1);
            indexOfStopCodon1= DNA.indexOf(stopCodons[0], indexOfStopCodon1+3);

        }
        System.out.println("-----------------------");

        while(indexOfStopCodon2 != -1){

            indexesOfTGA.add(indexOfStopCodon2);
            System.out.println("index of stop codon TGA: "+indexOfStopCodon2);
            indexOfStopCodon2= DNA.indexOf(stopCodons[1], indexOfStopCodon2+3);

        }
        System.out.println("-----------------------");

        while(indexOfStopCodon3 != -1){

            indexesOfTAG.add(indexOfStopCodon3);
            System.out.println("index of stop codon TAG: "+indexOfStopCodon3);
            indexOfStopCodon3= DNA.indexOf(stopCodons[2], indexOfStopCodon3+3);

        }
        System.out.println("LISTS OF CODONS:");

        System.out.println("ATG:");
        System.out.println(indexesOfATG);
        System.out.println("TAA:");

        System.out.println( indexesOfTAA);
        System.out.println("TGA:");

        System.out.println( indexesOfTGA);
        System.out.println("TAG:");

        System.out.println(indexesOfTAG);
        System.out.println("-----------------------");

////////////ATG SPLITTER LOOP

        for(int i=0; i<100;i++){
            if(i<indexesOfATG.size() && i< indexesOfTAG.size()){


                int tagIndexes= indexesOfTAG.get(i);
                int atgIndexes=(indexesOfATG.get(i)+3);
                if( (  indexesOfTAG.get(i) -  (indexesOfATG.get(i)+3) ) % 3 ==0 ){

                    System.out.println("its a gene! indexes (ATG, TAG):"+atgIndexes+" "+tagIndexes);
                }else{
                    System.out.println("err indexes (ATG, TAG):"+atgIndexes+" "+tagIndexes);
                }
            }


        }



        for(int i=0; i<100;i++){
            if(i<indexesOfTGA.size() && i< indexesOfTGA.size()){


                int tagIndexes= indexesOfTGA.get(i);
                int atgIndexes=(indexesOfATG.get(i)+3);
                if( (  indexesOfTGA.get(i) -  (indexesOfTGA.get(i)+3) ) % 3 ==0 ){

                    System.out.println("its a gene! indexes (ATG, TGA):"+atgIndexes+" "+tagIndexes);
                }else{
                    System.out.println("err indexes (ATG, TGA):"+atgIndexes+" "+tagIndexes);
                }
            }


        }

    }

}





